import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, ChevronDown, ChevronUp, AlertTriangle, CheckCircle, XCircle } from 'lucide-react';
import { useTheme } from '../contexts/ThemeContext';

const RiskBadge = ({ level }) => {
  const { colors: themeColors } = useTheme();
  
  const riskColors = {
    'Low': { bg: '#10B98120', text: '#10B981', border: '#10B98150' },
    'Medium': { bg: '#F59E0B20', text: '#F59E0B', border: '#F59E0B50' },
    'Medium-High': { bg: '#F9731620', text: '#F97316', border: '#F9731650' },
    'High': { bg: '#EF444420', text: '#EF4444', border: '#EF444450' }
  };

  const colorSet = riskColors[level];

  return (
    <span 
      className="px-2 py-1 rounded-full text-xs border"
      style={{
        backgroundColor: colorSet.bg,
        color: colorSet.text,
        borderColor: colorSet.border
      }}
    >
      {level} Risk
    </span>
  );
};

const AssetCard = ({ asset, index }) => {
  const { colors } = useTheme();
  const [isExpanded, setIsExpanded] = useState(false);
  const Icon = asset.icon;

  return (
    <motion.div
      className="rounded-lg border p-6 cursor-pointer hover:border-primary transition-colors duration-200"
      style={{
        backgroundColor: colors.card,
        borderColor: colors.border
      }}
      initial={{ y: 20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
      whileHover={{ scale: 1.02 }}
      onClick={() => setIsExpanded(!isExpanded)}
    >
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-4 flex-1">
          <div 
            className="p-3 rounded-lg"
            style={{ backgroundColor: `${colors.primary}20` }}
          >
            <Icon className="w-6 h-6 text-primary" />
          </div>
          <div className="flex-1">
            <div className="flex items-center justify-between mb-2">
              <h4 className="text-lg font-semibold" style={{ color: colors.text.primary }}>
                {asset.title}
              </h4>
              <RiskBadge level={asset.riskLevel} />
            </div>
            <p className="text-sm mb-3" style={{ color: colors.text.secondary }}>
              {asset.description}
            </p>
            
            <div className="flex flex-wrap gap-2 mb-3">
              {asset.examples.map((example, idx) => (
                <span 
                  key={idx} 
                  className="px-2 py-1 rounded text-xs"
                  style={{
                    backgroundColor: colors.surface,
                    color: colors.text.secondary
                  }}
                >
                  {example}
                </span>
              ))}
            </div>
          </div>
        </div>
        
        <button 
          className="hover:text-primary transition-colors"
          style={{ color: colors.text.secondary }}
        >
          {isExpanded ? <ChevronUp className="w-5 h-5" /> : <ChevronDown className="w-5 h-5" />}
        </button>
      </div>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="overflow-hidden"
          >
            <div className="pt-4 mt-4 border-t" style={{ borderColor: colors.border }}>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <div className="flex items-center space-x-2 mb-2">
                    <CheckCircle className="w-4 h-4" style={{ color: colors.success }} />
                    <h5 className="font-medium" style={{ color: colors.success }}>Pros</h5>
                  </div>
                  <ul className="space-y-1">
                    {asset.pros.map((pro, idx) => (
                      <li key={idx} className="text-sm flex items-start space-x-2" style={{ color: colors.text.secondary }}>
                        <span 
                          className="w-1 h-1 rounded-full mt-2 flex-shrink-0"
                          style={{ backgroundColor: colors.success }}
                        ></span>
                        <span>{pro}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                
                <div>
                  <div className="flex items-center space-x-2 mb-2">
                    <XCircle className="w-4 h-4" style={{ color: colors.danger }} />
                    <h5 className="font-medium" style={{ color: colors.danger }}>Cons</h5>
                  </div>
                  <ul className="space-y-1">
                    {asset.cons.map((con, idx) => (
                      <li key={idx} className="text-sm flex items-start space-x-2" style={{ color: colors.text.secondary }}>
                        <span 
                          className="w-1 h-1 rounded-full mt-2 flex-shrink-0"
                          style={{ backgroundColor: colors.danger }}
                        ></span>
                        <span>{con}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

const HedgeRecommendations = ({ hedgeAssets, inflationRate }) => {
  return (
    <motion.div
      initial={{ y: 20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.4 }}
      className="mt-12"
    >
      <div className="flex items-center space-x-3 mb-6">
        <div className="w-10 h-10 rounded-lg flex items-center justify-center" style={{ backgroundColor: '#3B82F6' }}>
          <Shield className="w-5 h-5 text-white" />
        </div>
        <h2 className="text-2xl font-bold text-gray-900">
          Inflation Hedge Recommendations
        </h2>
      </div>
      
      <div className="border rounded-xl p-4 mb-6" style={{
        backgroundColor: '#EFF6FF',
        borderColor: '#BFDBFE'
      }}>
        <div className="flex items-start space-x-3">
          <AlertTriangle className="w-5 h-5 text-blue-600 mt-0.5" />
          <div>
            <p className="text-sm text-gray-900">
              <strong>Current inflation rate: {inflationRate}%</strong>
            </p>
            <p className="text-sm mt-1 text-gray-600">
              Consider these assets to protect your purchasing power against inflation. 
              Remember to diversify and consult with a financial advisor.
            </p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {hedgeAssets.map((asset, index) => (
          <AssetCard key={asset.id} asset={asset} index={index} />
        ))}
      </div>
    </motion.div>
  );
};

export default HedgeRecommendations;
