import React, { useState, useMemo } from 'react';
import { motion } from 'framer-motion';
import Header from './components/Header';
import CountrySelector from './components/CountrySelector';
import YearSelector from './components/YearSelector';
import CurrencySelector from './components/CurrencySelector';
import InflationChart from './components/InflationChart';
import InflationStats from './components/InflationStats';
import InflationCalculator from './components/InflationCalculator';
import HedgeRecommendations from './components/HedgeRecommendations';
import { inflationData, countries, currencyRates } from './data/inflationData';
import { hedgeAssets } from './data/hedgeAssets';

function App() {
  const [selectedCountry, setSelectedCountry] = useState('Indonesia');
  const [chartType, setChartType] = useState('line');

  const currentCountryData = inflationData[selectedCountry];

  // Use all available data for the chart
  const convertedData = currentCountryData.data;

  const currentInflationRate = convertedData[convertedData.length - 1]?.rate || 0;

  const handleCountryChange = (country) => {
    setSelectedCountry(country);
  };

  return (
    <div className="min-h-screen bg-dark-200">
      <Header />
      
      <main className="max-w-7xl mx-auto px-6 py-8">
        {/* Controls Section */}
        <motion.div 
          className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8"
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <CountrySelector
            countries={countries}
            selectedCountry={selectedCountry}
            onCountryChange={handleCountryChange}
            countryData={inflationData}
          />
          <div></div>
          <div></div>
        </motion.div>

        {/* Chart Section */}
        <div className="mb-8">
          <InflationChart
            data={convertedData}
            selectedYear={selectedYear}
            chartType={chartType}
            onChartTypeChange={setChartType}
          />
        </div>

        {/* Calculator Section */}
        <InflationCalculator
          countryData={currentCountryData}
          selectedCountry={selectedCountry}
        />

        {/* Recommendations Section */}
        <HedgeRecommendations
          hedgeAssets={hedgeAssets}
          inflationRate={currentInflationRate.toFixed(2)}
        />
      </main>
    </div>
  );
}

export default App;
