import React from 'react';
import { motion } from 'framer-motion';
import { TrendingUp, Sun, Moon } from 'lucide-react';
import { useTheme } from '../contexts/ThemeContext';

const Header = () => {
  const { isDark, toggleTheme, colors } = useTheme();

  return (
    <motion.header 
      className="border-b px-6 py-4 transition-colors duration-200"
      style={{ 
        backgroundColor: colors.surface,
        borderColor: colors.border
      }}
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="bg-primary p-2 rounded-lg">
            <TrendingUp className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-2xl font-bold" style={{ color: colors.text.primary }}>
              Inflate
            </h1>
            <p className="text-sm" style={{ color: colors.text.secondary }}>
              Track, Understand, and Outsmart Inflation
            </p>
          </div>
        </div>
        
        <div className="flex items-center space-x-6">
          <nav className="hidden md:flex items-center space-x-6">
            <a 
              href="#dashboard" 
              className="transition-colors hover:text-primary"
              style={{ color: colors.text.primary }}
            >
              Dashboard
            </a>
            <a 
              href="#calculator" 
              className="transition-colors hover:text-primary"
              style={{ color: colors.text.primary }}
            >
              Calculator
            </a>
            <a 
              href="#recommendations" 
              className="transition-colors hover:text-primary"
              style={{ color: colors.text.primary }}
            >
              Recommendations
            </a>
          </nav>
          
          {/* Theme Toggle Button */}
          <button
            onClick={toggleTheme}
            className="p-2 rounded-lg transition-all duration-200 hover:scale-105"
            style={{ 
              backgroundColor: colors.card,
              border: `1px solid ${colors.border}`
            }}
            title={isDark ? 'Switch to light mode' : 'Switch to dark mode'}
          >
            {isDark ? (
              <Sun className="w-5 h-5" style={{ color: colors.text.primary }} />
            ) : (
              <Moon className="w-5 h-5" style={{ color: colors.text.primary }} />
            )}
          </button>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
