import React from 'react';
import { motion } from 'framer-motion';
import { ChevronDown, Globe } from 'lucide-react';

const CountrySelector = ({ countries, selectedCountry, onCountryChange, countryData }) => {
  return (
    <motion.div 
      className="mb-6"
      initial={{ scale: 0.95, opacity: 0 }}
      animate={{ scale: 1, opacity: 1 }}
      transition={{ duration: 0.3 }}
    >
      <div className="relative">
        <select
          value={selectedCountry}
          onChange={(e) => onCountryChange(e.target.value)}
          className="w-full bg-white border border-gray-300 rounded-lg px-4 py-3 text-gray-700 focus:border-primary focus:outline-none appearance-none cursor-pointer text-sm"
        >
          {countries.map((country) => (
            <option key={country} value={country} className="bg-white">
              {countryData[country]?.flag} {country}
            </option>
          ))}
        </select>
        <ChevronDown className="absolute right-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
      </div>
    </motion.div>
  );
};

export default CountrySelector;
