import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Calculator, ArrowRight, TrendingUp, Globe, ChevronDown } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Area, AreaChart } from 'recharts';
import { useTheme } from '../contexts/ThemeContext';

const InflationCalculator = ({ countryData }) => {
  const { colors } = useTheme();
  const [amount, setAmount] = useState('100');
  const [startYear, setStartYear] = useState(2000);
  const [endYear, setEndYear] = useState(2025);
  const [result, setResult] = useState(null);
  const [showStartYearPopup, setShowStartYearPopup] = useState(false);
  const [showEndYearPopup, setShowEndYearPopup] = useState(false);

  // Format number with thousands separator (dots)
  const formatInputAmount = (value) => {
    if (!value) return '';
    // Remove all non-digits
    const numericValue = value.toString().replace(/\D/g, '');
    // Add dots as thousands separator
    return numericValue.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
  };

  // Handle amount input change
  const handleAmountChange = (e) => {
    const inputValue = e.target.value;
    // Remove all dots and non-digits, keep only numbers
    const numericValue = inputValue.replace(/\D/g, '');
    setAmount(numericValue);
  };

  const availableYears = countryData.data.map(item => item.year).sort();
  const minYear = Math.min(...availableYears);
  const maxYear = Math.max(...availableYears);

  const calculateInflation = () => {
    if (!amount || startYear >= endYear) return;

    const startAmount = parseFloat(amount);
    let currentValue = startAmount;
    const yearlyValues = [{ year: startYear, value: startAmount, rate: 0 }];

    // Calculate compound inflation from start year to end year
    for (let year = startYear + 1; year <= endYear; year++) {
      const yearData = countryData.data.find(item => item.year === year);
      if (yearData) {
        const inflationRate = yearData.rate / 100;
        currentValue = currentValue * (1 + inflationRate);
        yearlyValues.push({ 
          year, 
          value: currentValue, 
          rate: yearData.rate 
        });
      }
    }

    const totalInflation = ((currentValue - startAmount) / startAmount) * 100;
    const purchasingPowerLoss = ((startAmount / currentValue) * 100) - 100;
    const averageInflation = totalInflation / (endYear - startYear);

    // Get inflation rates for the period
    const inflationRates = countryData.data
      .filter(item => item.year >= startYear && item.year <= endYear)
      .map(item => ({ year: item.year, rate: item.rate }));

    setResult({
      originalAmount: startAmount,
      inflatedAmount: currentValue,
      totalInflation: totalInflation,
      purchasingPowerLoss: Math.abs(purchasingPowerLoss),
      yearRange: endYear - startYear,
      averageInflation: averageInflation,
      yearlyValues: yearlyValues,
      inflationRates: inflationRates
    });
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: countryData.currency,
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  return (
    <motion.div
      className="rounded-lg border p-6 mt-8"
      style={{
        backgroundColor: colors.card,
        borderColor: colors.border
      }}
      initial={{ y: 20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.5 }}
    >
      <div className="flex items-center space-x-3 mb-6">
        <Calculator className="w-6 h-6 text-primary" />
        <h2 className="text-2xl font-bold" style={{ color: colors.text.primary }}>
          Inflation Calculator
        </h2>
      </div>

      <p className="mb-6" style={{ color: colors.text.secondary }}>
        Calculate how inflation affects the purchasing power of your money over time in {selectedCountry}.
      </p>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        {/* Country Display */}
        <div>
          <label className="block text-sm font-medium mb-2" style={{ color: colors.text.secondary }}>
            <Globe className="w-4 h-4 inline mr-2" />
            Country
          </label>
          <div 
            className="w-full rounded-lg px-4 py-3 border flex items-center"
            style={{
              backgroundColor: colors.surface,
              border: `1px solid ${colors.border}`,
              color: colors.text.primary
            }}
          >
            <span className="text-lg mr-2">ðŸ‡®ðŸ‡©</span>
            <span>Indonesia</span>
          </div>
        </div>

        {/* Amount Input */}
        <div>
          <label className="block text-sm font-medium mb-2" style={{ color: colors.text.secondary }}>
            Amount ({countryData.currencySymbol})
          </label>
          <input
            type="text"
            value={formatInputAmount(amount)}
            onChange={handleAmountChange}
            className="w-full rounded-lg px-4 py-3 focus:border-primary focus:outline-none"
            style={{
              backgroundColor: colors.surface,
              border: `1px solid ${colors.border}`,
              color: colors.text.primary,
              // Hide number input arrows
              MozAppearance: 'textfield',
              WebkitAppearance: 'none',
            }}
            placeholder="Enter amount"
          />
          <style jsx>{`
            input[type="text"]::-webkit-outer-spin-button,
            input[type="text"]::-webkit-inner-spin-button {
              -webkit-appearance: none;
              margin: 0;
            }
            input[type="text"] {
              -moz-appearance: textfield;
            }
          `}</style>
        </div>

        {/* Start Year */}
        <div className="relative">
          <label className="block text-sm font-medium mb-2" style={{ color: colors.text.secondary }}>
            Start Year
          </label>
          <button
            type="button"
            onClick={() => setShowStartYearPopup(true)}
            className="w-full rounded-lg px-4 py-3 text-center font-medium border focus:border-primary focus:outline-none hover:border-primary transition-colors"
            style={{
              backgroundColor: colors.surface,
              border: `1px solid ${colors.border}`,
              color: colors.text.primary
            }}
          >
            {startYear}
          </button>
          
          {/* Start Year Popup */}
          <AnimatePresence>
            {showStartYearPopup && (
              <motion.div 
                className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={() => setShowStartYearPopup(false)}
              >
              <motion.div 
                className="rounded-xl shadow-2xl w-full max-w-md max-h-[80vh] overflow-hidden"
                style={{ backgroundColor: colors.card, border: `1px solid ${colors.border}` }}
                initial={{ scale: 0.9, opacity: 0, y: 20 }}
                animate={{ scale: 1, opacity: 1, y: 0 }}
                exit={{ scale: 0.9, opacity: 0, y: 20 }}
                onClick={(e) => e.stopPropagation()}
              >
                {/* Header */}
                <div className="px-6 py-4 border-b" style={{ borderColor: colors.border }}>
                  <h3 className="text-xl font-bold" style={{ color: colors.text.primary }}>
                    Select Start Year
                  </h3>
                  <p className="text-sm mt-1" style={{ color: colors.text.secondary }}>
                    Choose the starting year for inflation calculation
                  </p>
                </div>
                
                {/* Content */}
                <div className="p-6 max-h-96 overflow-y-auto">
                  <div className="grid grid-cols-5 gap-2">
                    {availableYears.filter(year => year < maxYear).map(year => (
                      <motion.button
                        key={year}
                        onClick={() => {
                          setStartYear(year);
                          if (endYear <= year) {
                            setEndYear(year + 1);
                          }
                          setShowStartYearPopup(false);
                        }}
                        className={`p-3 rounded-lg text-sm font-semibold transition-all duration-200 ${
                          year === startYear 
                            ? 'bg-primary text-white shadow-lg transform scale-105' 
                            : 'hover:shadow-md hover:transform hover:scale-105'
                        }`}
                        style={year !== startYear ? { 
                          backgroundColor: colors.surface,
                          color: colors.text.primary,
                          border: `1px solid ${colors.border}`
                        } : {}}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        {year}
                      </motion.button>
                    ))}
                  </div>
                </div>
                
                {/* Footer */}
                <div className="px-6 py-4 border-t" style={{ borderColor: colors.border }}>
                  <button
                    onClick={() => setShowStartYearPopup(false)}
                    className="w-full py-3 px-4 rounded-lg font-medium transition-colors duration-200"
                    style={{
                      backgroundColor: colors.surface,
                      color: colors.text.primary,
                      border: `1px solid ${colors.border}`
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </motion.div>
            </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* End Year */}
        <div className="relative">
          <label className="block text-sm font-medium mb-2" style={{ color: colors.text.secondary }}>
            End Year
          </label>
          <button
            type="button"
            onClick={() => setShowEndYearPopup(true)}
            className="w-full rounded-lg px-4 py-3 text-center font-medium border focus:border-primary focus:outline-none hover:border-primary transition-colors"
            style={{
              backgroundColor: colors.surface,
              border: `1px solid ${colors.border}`,
              color: colors.text.primary
            }}
          >
            {endYear}
          </button>
          
          {/* End Year Popup */}
          <AnimatePresence>
            {showEndYearPopup && (
            <motion.div 
              className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setShowEndYearPopup(false)}
            >
              <motion.div 
                className="rounded-xl shadow-2xl w-full max-w-md max-h-[80vh] overflow-hidden"
                style={{ backgroundColor: colors.card, border: `1px solid ${colors.border}` }}
                initial={{ scale: 0.9, opacity: 0, y: 20 }}
                animate={{ scale: 1, opacity: 1, y: 0 }}
                exit={{ scale: 0.9, opacity: 0, y: 20 }}
                onClick={(e) => e.stopPropagation()}
              >
                {/* Header */}
                <div className="px-6 py-4 border-b" style={{ borderColor: colors.border }}>
                  <h3 className="text-xl font-bold" style={{ color: colors.text.primary }}>
                    Select End Year
                  </h3>
                  <p className="text-sm mt-1" style={{ color: colors.text.secondary }}>
                    Choose the ending year for inflation calculation
                  </p>
                </div>
                
                {/* Content */}
                <div className="p-6 max-h-96 overflow-y-auto">
                  <div className="grid grid-cols-5 gap-2">
                    {availableYears.filter(year => year > startYear).map(year => (
                      <motion.button
                        key={year}
                        onClick={() => {
                          setEndYear(year);
                          setShowEndYearPopup(false);
                        }}
                        className={`p-3 rounded-lg text-sm font-semibold transition-all duration-200 ${
                          year === endYear 
                            ? 'bg-primary text-white shadow-lg transform scale-105' 
                            : 'hover:shadow-md hover:transform hover:scale-105'
                        }`}
                        style={year !== endYear ? { 
                          backgroundColor: colors.surface,
                          color: colors.text.primary,
                          border: `1px solid ${colors.border}`
                        } : {}}
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                      >
                        {year}
                      </motion.button>
                    ))}
                  </div>
                </div>
                
                {/* Footer */}
                <div className="px-6 py-4 border-t" style={{ borderColor: colors.border }}>
                  <button
                    onClick={() => setShowEndYearPopup(false)}
                    className="w-full py-3 px-4 rounded-lg font-medium transition-colors duration-200"
                    style={{
                      backgroundColor: colors.surface,
                      color: colors.text.primary,
                      border: `1px solid ${colors.border}`
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </motion.div>
            </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Calculate Button */}
        <div className="flex items-end">
          <button
            onClick={calculateInflation}
            disabled={!amount || startYear >= endYear}
            className="w-full bg-primary hover:bg-blue-600 text-white px-4 py-3 rounded-lg transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center space-x-2"
          >
            <Calculator className="w-4 h-4" />
            <span>Calculate</span>
          </button>
        </div>
      </div>

      {/* Results */}
      {result && (
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          className="rounded-lg p-6 border"
          style={{
            backgroundColor: colors.surface,
            borderColor: colors.border
          }}
        >
          <h3 className="text-lg font-semibold mb-4" style={{ color: colors.text.primary }}>
            Calculation Results
          </h3>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="text-center">
              <p className="text-sm mb-1" style={{ color: colors.text.secondary }}>
                Original Amount ({startYear})
              </p>
              <p className="text-xl font-bold" style={{ color: colors.text.primary }}>
                {formatCurrency(result.originalAmount)}
              </p>
            </div>
            
            <div className="flex items-center justify-center">
              <ArrowRight className="w-6 h-6 text-primary" />
            </div>
            
            <div className="text-center">
              <p className="text-sm mb-1" style={{ color: colors.text.secondary }}>
                Equivalent Amount ({endYear})
              </p>
              <p className="text-xl font-bold" style={{ color: colors.danger }}>
                {formatCurrency(result.inflatedAmount)}
              </p>
            </div>
            
            <div className="text-center">
              <p className="text-sm mb-1" style={{ color: colors.text.secondary }}>
                Total Inflation
              </p>
              <p className="text-xl font-bold" style={{ color: colors.danger }}>
                +{result.totalInflation.toFixed(2)}%
              </p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div 
              className="rounded-lg p-4 border"
              style={{
                backgroundColor: `${colors.danger}10`,
                borderColor: `${colors.danger}30`
              }}
            >
              <div className="flex items-center space-x-2 mb-2">
                <TrendingUp className="w-5 h-5" style={{ color: colors.danger }} />
                <h4 className="font-semibold" style={{ color: colors.danger }}>
                  Purchasing Power Loss
                </h4>
              </div>
              <p className="text-2xl font-bold mb-2" style={{ color: colors.danger }}>
                -{result.purchasingPowerLoss.toFixed(2)}%
              </p>
              <p className="text-sm" style={{ color: colors.text.secondary }}>
                Your money lost {result.purchasingPowerLoss.toFixed(1)}% of its purchasing power over {result.yearRange} years.
              </p>
            </div>

            <div 
              className="rounded-lg p-4 border"
              style={{
                backgroundColor: `${colors.primary}10`,
                borderColor: `${colors.primary}30`
              }}
            >
              <h4 className="font-semibold mb-2" style={{ color: colors.primary }}>
                What this means:
              </h4>
              <p className="text-sm mb-2" style={{ color: colors.text.secondary }}>
                What cost {formatCurrency(result.originalAmount)} in {startYear} would cost approximately {formatCurrency(result.inflatedAmount)} in {endYear}.
              </p>
              <p className="text-sm" style={{ color: colors.text.secondary }}>
                To maintain the same purchasing power, you would need {((result.inflatedAmount / result.originalAmount - 1) * 100).toFixed(1)}% more money.
              </p>
            </div>
          </div>

          {/* Charts Section */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
            {/* Value Over Time Chart */}
            <div>
              <h4 className="text-lg font-semibold mb-4" style={{ color: colors.text.primary }}>
                Value Over Time
              </h4>
              <div className="h-64 rounded-lg p-4" style={{ backgroundColor: colors.surface }}>
                <ResponsiveContainer width="100%" height="100%">
                  <AreaChart data={result.yearlyValues}>
                    <defs>
                      <linearGradient id="colorValue" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#2962FF" stopOpacity={0.3}/>
                        <stop offset="95%" stopColor="#2962FF" stopOpacity={0.1}/>
                      </linearGradient>
                    </defs>
                    <CartesianGrid strokeDasharray="3 3" stroke={colors.border} />
                    <XAxis 
                      dataKey="year" 
                      stroke={colors.text.secondary}
                      fontSize={12}
                    />
                    <YAxis 
                      stroke={colors.text.secondary}
                      fontSize={12}
                      tickFormatter={(value) => formatCurrency(value).slice(0, -3) + 'k'}
                    />
                    <Tooltip 
                      contentStyle={{
                        backgroundColor: colors.card,
                        border: `1px solid ${colors.border}`,
                        borderRadius: '8px',
                        color: colors.text.primary
                      }}
                      formatter={(value) => [formatCurrency(value), 'Value']}
                    />
                    <Area 
                      type="monotone" 
                      dataKey="value" 
                      stroke="#2962FF" 
                      strokeWidth={2}
                      fillOpacity={1}
                      fill="url(#colorValue)"
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Inflation Rate Chart */}
            <div>
              <h4 className="text-lg font-semibold mb-4" style={{ color: colors.text.primary }}>
                Inflation Rate Timeline
              </h4>
              <div className="h-64 rounded-lg p-4" style={{ backgroundColor: colors.surface }}>
                <ResponsiveContainer width="100%" height="100%">
                  <LineChart data={result.inflationRates}>
                    <CartesianGrid strokeDasharray="3 3" stroke={colors.border} />
                    <XAxis 
                      dataKey="year" 
                      stroke={colors.text.secondary}
                      fontSize={12}
                    />
                    <YAxis 
                      stroke={colors.text.secondary}
                      fontSize={12}
                      tickFormatter={(value) => `${value}%`}
                    />
                    <Tooltip 
                      contentStyle={{
                        backgroundColor: colors.card,
                        border: `1px solid ${colors.border}`,
                        borderRadius: '8px',
                        color: colors.text.primary
                      }}
                      formatter={(value) => [`${value}%`, 'Inflation Rate']}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="rate" 
                      stroke="#EF4444" 
                      strokeWidth={2}
                      dot={{ fill: '#EF4444', strokeWidth: 2, r: 3 }}
                      activeDot={{ r: 5, fill: '#EF4444' }}
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>

          <div 
            className="mt-6 p-4 rounded-lg border"
            style={{
              backgroundColor: '#FEF3C7',
              borderColor: '#F59E0B'
            }}
          >
            <p className="text-sm" style={{ color: '#92400E' }}>
              <strong>Note:</strong> This calculation is based on historical inflation data for {selectedCountry}. 
              Actual inflation may vary and this tool is for educational purposes only.
            </p>
          </div>
        </motion.div>
      )}
    </motion.div>
  );
};

export default InflationCalculator;
