import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, ChevronDown, ChevronUp, AlertTriangle, CheckCircle, XCircle } from 'lucide-react';
import { useTheme } from '../contexts/ThemeContext';

const RiskBadge = ({ level }) => {
  const { colors: themeColors } = useTheme();
  
  const riskColors = {
    'Low': { bg: '#10B98120', text: '#10B981', border: '#10B98150' },
    'Medium': { bg: '#F59E0B20', text: '#F59E0B', border: '#F59E0B50' },
    'Medium-High': { bg: '#F9731620', text: '#F97316', border: '#F9731650' },
    'High': { bg: '#EF444420', text: '#EF4444', border: '#EF444450' }
  };

  const colorSet = riskColors[level];

  return (
    <span 
      className="px-2 py-1 rounded-full text-xs border"
      style={{
        backgroundColor: colorSet.bg,
        color: colorSet.text,
        borderColor: colorSet.border
      }}
    >
      {level} Risk
    </span>
  );
};

const AssetCard = ({ asset, index }) => {
  const { colors } = useTheme();
  const [isExpanded, setIsExpanded] = useState(false);
  const Icon = asset.icon;

  return (
    <motion.div
      className="rounded-lg border p-6 cursor-pointer hover:border-primary transition-colors duration-200"
      style={{
        backgroundColor: colors.card,
        borderColor: colors.border
      }}
      initial={{ y: 20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
      whileHover={{ scale: 1.02 }}
      onClick={() => setIsExpanded(!isExpanded)}
    >
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-4 flex-1">
          <div className="bg-primary/20 p-3 rounded-lg">
            <Icon className="w-6 h-6 text-primary" />
          </div>
          <div className="flex-1">
            <div className="flex items-center justify-between mb-2">
              <h4 className="text-lg font-semibold text-white">{asset.title}</h4>
              <RiskBadge level={asset.riskLevel} />
            </div>
            <p className="text-text-secondary text-sm mb-3">{asset.description}</p>
            
            <div className="flex flex-wrap gap-2 mb-3">
              {asset.examples.map((example, idx) => (
                <span key={idx} className="bg-dark-200 text-text-secondary px-2 py-1 rounded text-xs">
                  {example}
                </span>
              ))}
            </div>
          </div>
        </div>
        
        <button className="text-text-secondary hover:text-primary transition-colors">
          {isExpanded ? <ChevronUp className="w-5 h-5" /> : <ChevronDown className="w-5 h-5" />}
        </button>
      </div>

      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ height: 0, opacity: 0 }}
            animate={{ height: 'auto', opacity: 1 }}
            exit={{ height: 0, opacity: 0 }}
            transition={{ duration: 0.3 }}
            className="overflow-hidden"
          >
            <div className="pt-4 border-t border-gray-700 mt-4">
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <div className="flex items-center space-x-2 mb-2">
                    <CheckCircle className="w-4 h-4 text-success" />
                    <h5 className="font-medium text-success">Pros</h5>
                  </div>
                  <ul className="space-y-1">
                    {asset.pros.map((pro, idx) => (
                      <li key={idx} className="text-sm text-text-secondary flex items-start space-x-2">
                        <span className="w-1 h-1 bg-success rounded-full mt-2 flex-shrink-0"></span>
                        <span>{pro}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                
                <div>
                  <div className="flex items-center space-x-2 mb-2">
                    <XCircle className="w-4 h-4 text-danger" />
                    <h5 className="font-medium text-danger">Cons</h5>
                  </div>
                  <ul className="space-y-1">
                    {asset.cons.map((con, idx) => (
                      <li key={idx} className="text-sm text-text-secondary flex items-start space-x-2">
                        <span className="w-1 h-1 bg-danger rounded-full mt-2 flex-shrink-0"></span>
                        <span>{con}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

const HedgeRecommendations = ({ hedgeAssets, inflationRate }) => {
  const { colors } = useTheme();

  return (
    <motion.div
      initial={{ y: 20, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5, delay: 0.4 }}
      className="mt-12"
    >
      <div className="flex items-center space-x-3 mb-6">
        <Shield className="w-6 h-6 text-primary" />
        <h2 className="text-2xl font-bold" style={{ color: colors.text.primary }}>
          Inflation Hedge Recommendations
        </h2>
      </div>
      
      <div className="border rounded-lg p-4 mb-6" style={{
        backgroundColor: `${colors.primary}10`,
        borderColor: `${colors.primary}30`
      }}>
        <div className="flex items-start space-x-3">
          <AlertTriangle className="w-5 h-5 text-primary mt-0.5" />
          <div>
            <p className="text-sm" style={{ color: colors.text.primary }}>
              <strong>Current inflation rate: {inflationRate}%</strong>
            </p>
            <p className="text-sm mt-1" style={{ color: colors.text.secondary }}>
              Consider these assets to protect your purchasing power against inflation. 
              Remember to diversify and consult with a financial advisor.
            </p>
          </div>
        </div>
      </div>

      <div className="grid gap-4">
        {hedgeAssets.map((asset, index) => (
          <AssetCard key={asset.id} asset={asset} index={index} />
        ))}
      </div>
    </motion.div>
  );
};

export default HedgeRecommendations;
