import React, { useState } from 'react';
import { motion } from 'framer-motion';
import Header from './components/Header';
import InflationCalculator from './components/InflationCalculator';
import HedgeRecommendations from './components/HedgeRecommendations';
import { ThemeProvider, useTheme } from './contexts/ThemeContext';
import { inflationData, countries } from './data/inflationData';
import { hedgeAssets } from './data/hedgeAssets';

const AppContent = () => {
  const { colors } = useTheme();
  const [selectedCountry, setSelectedCountry] = useState('Indonesia');

  const currentCountryData = inflationData[selectedCountry];
  const currentInflationRate = currentCountryData.data[currentCountryData.data.length - 1]?.rate || 0;

  const handleCountryChange = (country) => {
    setSelectedCountry(country);
  };

  return (
    <div className="min-h-screen transition-colors duration-200" style={{ backgroundColor: colors.background }}>
      <Header />
      
      <main className="max-w-7xl mx-auto px-6 py-8">


        {/* Calculator Section */}
        <InflationCalculator
          countryData={currentCountryData}
          selectedCountry={selectedCountry}
          onCountryChange={handleCountryChange}
          countries={countries}
          inflationData={inflationData}
        />

        {/* Recommendations Section */}
        <HedgeRecommendations
          hedgeAssets={hedgeAssets}
          inflationRate={currentInflationRate.toFixed(2)}
        />
      </main>
    </div>
  );
};

function App() {
  return (
    <ThemeProvider>
      <AppContent />
    </ThemeProvider>
  );
}

export default App;
