import React from 'react';
import { motion } from 'framer-motion';
import { TrendingUp, Search, User } from 'lucide-react';

const Header = () => {
  return (
    <motion.header 
      className="sticky top-0 z-50 border-b px-6 py-4 transition-colors duration-200"
      style={{ 
        backgroundColor: '#FFFFFF',
        borderColor: '#E5E7EB'
      }}
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="bg-blue-600 p-2 rounded-lg">
            <TrendingUp className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              Inflate
            </h1>
            <p className="text-sm text-gray-600">
              Track, Understand, and Outsmart Inflation
            </p>
          </div>
        </div>
        
        <div className="flex items-center space-x-4">
          {/* Search Button */}
          <button
            className="p-2 rounded-lg transition-all duration-200 hover:bg-gray-100 text-gray-600 hover:text-gray-900"
            title="Search"
          >
            <Search className="w-5 h-5" />
          </button>
          
          {/* Sign In Button */}
          <button
            className="flex items-center space-x-2 px-4 py-2 rounded-lg transition-all duration-200 bg-blue-600 hover:bg-blue-700 text-white font-medium"
          >
            <User className="w-4 h-4" />
            <span>Sign In</span>
          </button>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
