import React from 'react';
import { motion } from 'framer-motion';
import { TrendingUp } from 'lucide-react';

const Header = () => {
  return (
    <motion.header 
      className="bg-dark-200 border-b border-gray-700 px-6 py-4"
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="bg-primary p-2 rounded-lg">
            <TrendingUp className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-white">Inflate</h1>
            <p className="text-sm text-text-secondary">Track, Understand, and Outsmart Inflation</p>
          </div>
        </div>
        
        <nav className="hidden md:flex items-center space-x-6">
          <a href="#dashboard" className="text-text-primary hover:text-primary transition-colors">
            Dashboard
          </a>
          <a href="#analytics" className="text-text-primary hover:text-primary transition-colors">
            Analytics
          </a>
          <a href="#recommendations" className="text-text-primary hover:text-primary transition-colors">
            Recommendations
          </a>
        </nav>
      </div>
    </motion.header>
  );
};

export default Header;
