import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { TrendingUp, Search } from 'lucide-react';

const Header = () => {
  const [selectedLanguage, setSelectedLanguage] = useState('ID');
  const [showLanguageDropdown, setShowLanguageDropdown] = useState(false);

  const languages = [
    { code: 'ID', name: 'Indonesia', flag: 'ðŸ‡®ðŸ‡©' },
    { code: 'EN', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' }
  ];

  const currentLanguage = languages.find(lang => lang.code === selectedLanguage);

  return (
    <motion.header 
      className="sticky top-0 z-50 px-6 py-4 transition-colors duration-200"
      style={{ 
        backgroundColor: '#FFFFFF'
      }}
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="bg-blue-600 p-2 rounded-lg">
            <TrendingUp className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              Inflate
            </h1>
          </div>
        </div>
        
        <div className="flex items-center space-x-4">
          {/* Search Input */}
          <div className="relative">
            <div className="flex items-center rounded-full px-4 py-2 min-w-[280px]" style={{ backgroundColor: '#F9FAFB' }}>
              <Search className="w-5 h-5 text-gray-400 mr-3" />
              <input
                type="text"
                placeholder={selectedLanguage === 'ID' ? 'Cari indeks' : 'Search indexes'}
                className="bg-transparent flex-1 text-gray-600 placeholder-gray-400 focus:outline-none text-sm"
              />
              <div className="ml-3 bg-gray-300 text-gray-600 px-2 py-1 rounded text-xs font-medium">
                /
              </div>
            </div>
          </div>
          
          {/* Language Selector */}
          <div className="relative">
            <button
              onClick={() => setShowLanguageDropdown(!showLanguageDropdown)}
              className="flex items-center space-x-2 px-3 py-2 rounded-lg hover:bg-gray-50 transition-colors duration-200"
            >
              <span className="text-lg">{currentLanguage.flag}</span>
              <span className="text-sm font-medium text-gray-700">{currentLanguage.code}</span>
              <ChevronDown className="w-4 h-4 text-gray-500" />
            </button>
            
            {showLanguageDropdown && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-2 z-50">
                {languages.map((language) => (
                  <button
                    key={language.code}
                    onClick={() => {
                      setSelectedLanguage(language.code);
                      setShowLanguageDropdown(false);
                    }}
                    className={`w-full flex items-center space-x-3 px-4 py-2 text-left hover:bg-gray-50 transition-colors ${
                      selectedLanguage === language.code ? 'bg-blue-50 text-blue-600' : 'text-gray-700'
                    }`}
                  >
                    <span className="text-lg">{language.flag}</span>
                    <span className="text-sm font-medium">{language.name}</span>
                  </button>
                ))}
              </div>
            )}
          </div>

          {/* Subscribe Button */}
          <button
            className="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white font-medium rounded-full transition-all duration-200"
          >
            {selectedLanguage === 'ID' ? 'Berlangganan' : 'Subscribe'}
          </button>
          
          {/* Sign In Link */}
          <button
            className="text-gray-700 hover:text-gray-900 font-medium transition-colors duration-200"
          >
            {selectedLanguage === 'ID' ? 'Masuk' : 'Sign In'}
          </button>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
